import React, { useState, useEffect } from 'react';
import { List, X } from 'lucide-react';

const RecipeList = ({ recipes }) => (
  <ul className="space-y-4">
    {recipes.map((recipe) => (
      <li key={recipe.id} className="bg-white shadow rounded-lg p-4">
        <h3 className="text-lg font-semibold">{recipe.title}</h3>
        <p className="text-gray-600">{recipe.description}</p>
      </li>
    ))}
  </ul>
);

const AddRecipeForm = ({ onAddRecipe }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddRecipe({ title, description });
    setTitle('');
    setDescription('');
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Recipe Title"
        className="w-full p-2 border rounded"
        required
      />
      <textarea
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        placeholder="Recipe Description"
        className="w-full p-2 border rounded"
        required
      />
      <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
        Add Recipe
      </button>
    </form>
  );
};

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/recipes/');
      const data = await response.json();
      setRecipes(data);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const addRecipe = async (newRecipe) => {
    try {
      const response = await fetch('http://localhost:8000/api/recipes/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newRecipe),
      });
      if (response.ok) {
        fetchRecipes();
        setShowForm(false);
      }
    } catch (error) {
      console.error('Error adding recipe:', error);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">My Recipe Book</h1>
      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 flex items-center bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
      >
        {showForm ? <X size={20} /> : <List size={20} />}
        <span className="ml-2">{showForm ? 'Close Form' : 'Add New Recipe'}</span>
      </button>
      {showForm && <AddRecipeForm onAddRecipe={addRecipe} />}
      <RecipeList recipes={recipes} />
    </div>
  );
};

export default App;
