import React, { useState, useEffect } from 'react';
import { List, X } from 'lucide-react';
import RecipeList from './components/RecipeList';
import AddRecipeForm from './components/AddRecipeForm';
import { fetchRecipes, addRecipe } from './services/api';

const App = () => {
  const [recipes, setRecipes] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    loadRecipes();
  }, []);

  const loadRecipes = async () => {
    try {
      const data = await fetchRecipes();
      setRecipes(data);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const handleAddRecipe = async (newRecipe) => {
    try {
      await addRecipe(newRecipe);
      await loadRecipes();
      setShowForm(false);
    } catch (error) {
      console.error('Error adding recipe:', error);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">My Recipe Book</h1>
      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 flex items-center bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
      >
        {showForm ? <X size={20} /> : <List size={20} />}
        <span className="ml-2">{showForm ? 'Close Form' : 'Add New Recipe'}</span>
      </button>
      {showForm && <AddRecipeForm onAddRecipe={handleAddRecipe} />}
      <RecipeList recipes={recipes} />
    </div>
  );
};

export default App;
